    data={this.state.data}
    renderItem={this.renderItem}
    // keyExtractor={item => item.pdfLink}
  />
:
<Text>Loading...</Text>
}

  </ScrollView> 
  ) 

  }
}

const mapStatetoProps = state => {
  console.log({ state });
