import React from "react";
import {
  Text,
  View,
  StyleSheet,
  Image,
  ScrollView,
  TextInput,
  TouchableOpacity,
  ImageBackground,
  Button
} from "react-native";
// import { Button } from "native-base";
import * as firebase from "firebase";

export default class OnlineClass extends React.Component {
  state = {
    className: "",
    course: "",
    timing: "",
    zoomLink: ""
  };

  uploadNewClass = () => {
    const { className, course, time, zoomLink } = this.state;
    const db = firebase.firestore().collection("onlineclasses");
    db.add({
      className,
      course,
      timing,
      zoomLink,
      uploadTime: firebase.firestore.Timestamp.now()
    });
  };

  render() {
    return (
      <View style={styles.container}>
        <Text style={styles.htext}>Online Class</Text>
        <View>
          <Text>Class</Text>
          <TextInput
            style={styles.inputBox}
            underlineColorAndroid="rgba(0,0,0,0)"
            placeholder="Enter Class"
            // placeholderTextColor="#ffffff"
            selectionColor="#fff"
            keyboardType="default"
            value={this.state.className}
            required
            onChangeText={text => this.setState({ className: text })}
          />
        </View>
        <View>
          <Text>Course</Text>
          <TextInput
            style={styles.inputBox}
            underlineColorAndroid="rgba(0,0,0,0)"
            placeholder="Enter Course"
            // placeholderTextColor="#ffffff"
            selectionColor="#fff"
            keyboardType="default"
            value={this.state.course}
            onChangeText={text => this.setState({ course: text })}
          />
        </View>
        <View>
          <Text>Time</Text>
          <TextInput
            style={styles.inputBox}
            underlineColorAndroid="rgba(0,0,0,0)"
            placeholder="Class Timing"
            // placeholderTextColor="#ffffff"
            selectionColor="#fff"
            keyboardType="default"
            value={this.state.timing}
            onChangeText={text => this.setState({ timing: text })}
          />
        </View>
        <View>
          <Text>Zoom Link</Text>
          <TextInput
            style={styles.inputBox}
            underlineColorAndroid="rgba(0,0,0,0)"
            placeholder="Paste Zoom Link Here"
            // placeholderTextColor="#ffffff"
            selectionColor="#fff"
            keyboardType="default"
            value={this.state.zoomLink}
            onChangeText={text => this.setState({ zoomLink: text })}
          />
        </View>
        <View style={styles.btnView}>
          <Button
            style={styles.btn}
            onPress={this.uploadNewClass}
            title="Submit"
            color="#2196F3"
            accessibilityLabel="Learn more about this purple button"
          />
        </View>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    marginLeft: 15,
    marginRight: 15
  },
  btnView: {
    marginVertical: 10
  },
  btnText: {
    color: "white"
  },
  htext: {
    marginTop: 18,
    marginBottom: 52,
    fontWeight: "bold",
    fontSize: 26,
    marginHorizontal: 62
  },
  inputBox: {
    width: "100%",
    backgroundColor: "#ffffff",
    borderRadius: 5,
    border: "1px solid grey",
    paddingHorizontal: 16,
    paddingVertical: 10,
    fontSize: 16,
    // color: "#efefef",
    marginVertical: 10
  }
});
