import * as React from 'react';
import { Text, View, StyleSheet, Image,ScrollView,TextInput,TouchableOpacity,ImageBackground,Alert } from 'react-native';
import firebase from '../config/fire'
export default class TutorLogin extends React.Component {
state={
  email:"",
  password:""
}
static navigationOptions={
  title:"Login"
}
userSignin(email,pass){
  console.log('rel',firebase)
  Alert.alert(email)

  firebase.auth().signInWithEmailAndPassword(email,pass)
  .then((res)=>{
    console.log(res)
    this.props.navigation.replace("tutorlogin")
  })
  .catch(error=>{
    Alert.alert(error.message)

  })
}



  render() {
    return (
      
      
      <ImageBackground  style={styles.container}  source={require('../assets/image/tutorpic.jpg')}>
      <ScrollView >
                                  
        <Image
          style={styles.logo}
          source={require('../assets/image/logo.jpeg')}
        />
    <Text style={styles.logotext}>The Best Place to Learn</Text>

      <Text style={styles.logintext}>Tutor Login</Text>
       <TextInput style={styles.inputBox}  underlineColorAndroid='rgba(0,0,0,0)' 
              placeholder="Email"
              placeholderTextColor = "#ffffff"
              selectionColor="#fff"
              keyboardType="email-address"
              value={this.state.email}
              onChangeText={(text)=>this.setState({email:text})}
              />
       
       <TextInput style={styles.inputBox} 
              underlineColorAndroid='rgba(0,0,0,0)' 
              placeholder="Password"
              secureTextEntry={true}
              placeholderTextColor = "#ffffff"
              value={this.state.password}
              onChangeText={(text)=>{this.setState({password:text})}}
              
              /> 

       

                 <TouchableOpacity style={styles.button} onPress={()=>{
                   this.userSignin(this.state.email,this.state.password)
                 }} >
             <Text style={styles.buttonText}>Login</Text>
           </TouchableOpacity> 
       
       <Text style={{textAlign:'center'}}>Don't have any account? </Text>
       <TouchableOpacity    onPress={() => this.props.navigation.navigate('tutorsignup')} >
       <Text style={styles.touchtext} >Signup</Text>
      </TouchableOpacity>
      
      
      <TouchableOpacity onPress={() => this.props.navigation.navigate('studentlogin')}>
       <Text style={styles.touchtext} >Are You Student?</Text> 
     </TouchableOpacity>
     </ScrollView>
       </ImageBackground>
       
       

    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex:1,
   alignItems: 'center',
    
   justifyContent:'center',
    
  },
  
  logo: {
    marginTop:16,
    height:150,
    width: 150,
    marginHorizontal:66,
    borderRadius:16,
    alignItems: 'center',
    textAlign:'center'
  },
  logotext: {
    marginTop:18,
    fontSize:18,
    color:'red',
     alignItems: 'center',
     textAlign:'center'
  },

  inputBox: {
    width:300,
    backgroundColor:'grey',
    borderRadius: 25,
    paddingHorizontal:16,
    fontSize:16,
    color:'#ffffff',
    marginVertical: 3,
  
  },
   button: {
    width:300,
    backgroundColor:"#2196F3",
     borderRadius: 25,
      marginVertical: 10,
      paddingVertical: 13
  },
  buttonText: {
    fontSize:16,
    fontWeight:'500',
    color:'white',
    textAlign:'center'
  },
  
    logintext:{
     color:'black',
      fontWeight: 'bold',
      fontSize: 26, 
       marginTop:18,
       paddingBottom:13,
       textAlign:'center'
    },
    
    touchtext:{
      paddingTop:13,
      color:'blue',
      textAlign:'center'
    }

});
