import React from "react";
import {
  Text,
  View,
  StyleSheet,
  Image,
  ScrollView,
  TextInput,
  TouchableOpacity,
  ImageBackground,
  Button
} from "react-native";
// import { Button } from "native-base";
import * as firebase from "firebase";

export default class OnlineClass extends React.Component {
  state = {
    className: "",
    course: "",
    timing: "",
    zoomLink: "",
    disabled: true
  };

  onChange = e => {
    console.log({ e: e.target });
    this.setState({ [e.target.name]: e.target.value }, () => {
      const { className, course, timing, zoomLink } = this.state;
      if (className && course && timing && zoomLink) {
        this.setState({ disabled: false });
      } else this.setState({ disabled: true });
    });
  };

  uploadNewClass = () => {
    console.log("clicked", this.state);
    const { className, course, timing, zoomLink } = this.state;
    const db = firebase.firestore().collection("onlineclasses");
    db.add({
      className,
      course,
      timing,
      zoomLink,
      uploadTime: firebase.firestore.Timestamp.now()
    });
  };

  render() {
    return (
      <View style={styles.container}>
        <Text style={styles.htext}>Online Class</Text>
        <View>
          <Text>Class</Text>
          <TextInput
            style={styles.inputBox}
            underlineColorAndroid="rgba(0,0,0,0)"
            placeholder="Enter Class"
            name="className"
            // placeholderTextColor="#ffffff"
            selectionColor="#fff"
            keyboardType="default"
            value={this.state.className}
            required
            onChange={this.onChange}
          />
        </View>
        <View>
          <Text>Course</Text>
          <TextInput
            style={styles.inputBox}
            underlineColorAndroid="rgba(0,0,0,0)"
            placeholder="Enter Course"
            name="course"
            // placeholderTextColor="#ffffff"
            selectionColor="#fff"
            keyboardType="default"
            value={this.state.course}
            onChange={this.onChange}
          />
        </View>
        <View>
          <Text>Time</Text>
          <TextInput
            style={styles.inputBox}
            underlineColorAndroid="rgba(0,0,0,0)"
            placeholder="Class Timing"
            name="timing"
            // placeholderTextColor="#ffffff"
            selectionColor="#fff"
            keyboardType="default"
            value={this.state.timing}
            onChange={this.onChange}
          />
        </View>
        <View>
          <Text>Zoom Link</Text>
          <TextInput
            style={styles.inputBox}
            underlineColorAndroid="rgba(0,0,0,0)"
            placeholder="Paste Zoom Link Here"
            name="zoomLink"
            // placeholderTextColor="#ffffff"
            selectionColor="#fff"
            keyboardType="default"
            value={this.state.zoomLink}
            onChange={this.onChange}
          />
        </View>
        <View style={styles.btnView}>
          <Button
            style={styles.btn}
            onPress={this.uploadNewClass}
            title="Submit"
            color="#2196F3"
            disabled={this.state.disabled}
          />
        </View>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    marginLeft: 15,
    marginRight: 15
  },
  btnView: {
    marginVertical: 10
  },
  btnText: {
    color: "white"
  },
  htext: {
    marginTop: 18,
    marginBottom: 52,
    fontWeight: "bold",
    fontSize: 26,
    marginHorizontal: 62
  },
  inputBox: {
    width: "100%",
    backgroundColor: "#ffffff",
    borderRadius: 5,
    border: "1px solid grey",
    paddingHorizontal: 16,
    paddingVertical: 10,
    fontSize: 16,
    // color: "#efefef",
    marginVertical: 10
  }
});
