import React from 'react';
import { Avatar, Button, Card, Title, Paragraph,} from "react-native-paper";
import {View,Text,FlatList, StyleSheet} from 'react-native'
import firebase from "../config/fire";

export default class AvailableTutors extends React.Component{
    state={
        data:[]
    }
    componentDidMount() {
        let arr =[]
         firebase.firestore().collection("tutors")
        .get().then(res=>{
            res.docs.forEach(doc => {
                let items = doc.data();
      
                /* Make data suitable for rendering */
                // items = JSON.stringify(items);
                arr.push(items)
                /* Update the components state with query result */
                console.log(items,'av')
                setTimeout(() => {
                    this.setState({data:arr})                
                }, 100);
                // this.setState({ items : items }) 
            });   
             })
             .catch(err => console.log(err.message))
    }

    renderItem = ({ item, index }) => {
        // let createDate = new Date(item.uploadTime.seconds * 1000);
    console.log()
        return (
          <Card style={styles.card}>
            <Card.Content>
              <Title>Quiz {index + 1} </Title>
              <Paragraph>
                Uploaded: 
              </Paragraph>
              {/* <Paragraph>Time & Date</Paragraph> */}
            </Card.Content>
            {/* <Card.Cover source={{ uri: "https://picsum.photos/700" }} /> */}
            <Card.Actions>
              <Button onPress={this.downloadPDF}>DOWNLOAD</Button>
            </Card.Actions>
          </Card>
        );
      };
    
    render(){
        console.log(this.state.data)
        const {data} = this.state
        return(
            <View>
            <Text>HElo</Text>
          {data.length>0&&
          <FlatList
            data={data}
            renderItem={this.renderItem}
            // keyExtractor={item => item.pdfLink}
          />
    }
        
          </View> )
    }
}

const styles = StyleSheet.create({
    card: {
      marginVertical: 10
    }
  });
