//     screen: TutorProfile
//   },
//   onlineclass: {
//     screen: OnlineClass
//   },
//   studentlogin: {
//     screen: StudentLogin
//   },
//   studentregister: {
//     screen: StudentRegister
//   },
//   tutorsignup: {
//     screen: TutorSignUp
//   },
//   studentsignup: {
//     screen: StudentSignUp
//   }
// });

// const AppContainer = createAppContainer(withNavigation(navigator));
export default App;
