import React from "react";
import firebase from "../../config/fire";
import {
  Text,
  View,
  StyleSheet,
  Image,
  ScrollView,
  TextInput,
  TouchableOpacity,
  ImageBackground,
  Alert
} from "react-native";
import { Avatar, Button, Card, Title, Paragraph } from "react-native-paper";

export default class QuizUpload extends React.Component {
  state = {};

  pickDocumentPDF = async () => {
    try {
      const res = await DocumentPicker.getDocumentAsync({
        type: "application/pdf"
      });
      //Printing the log realted to the file
      console.log("res : " + JSON.stringify(res));
      console.log("Type : " + res.type);
      if (res.type === "success") {
        let getTime = new Date().getTime();
        let storageRef = firebase.storage().ref();
        let ref = storageRef.child(`/quiz/${getTime}/${res.name}`);
        ref
          .putString(message, "data_url")
          .then(function(snapshot) {
            console.log("Uploaded a data_url string!");
          })
          .catch(err => {
            console.error(err.message);
            Alert.alert(err.message);
          });
      }
      // console.log("File Name : " + res.name);
      // console.log("File Size : " + res.size);
      //Setting the state to show single file attributes
      // setSingleFile(res);
    } catch (err) {
      alert("Unknown Error: " + JSON.stringify(err));
      throw err;
    }
  };

  render() {
    return (
      <View>
        <Card>
          <Card.Title />
          <Card.Content>
            <Title>Qiuz Uploads </Title>
            <Paragraph>Time & Date</Paragraph>
          </Card.Content>
          <Card.Cover
            source={{
              uri:
                "http://wccftech.com/wp-content/uploads/2014/10/JavaScript.pdf"
            }}
          />
          <Card.Actions>
            <Button>Upload</Button>
            <Button>Ok</Button>
          </Card.Actions>
        </Card>
      </View>
    );
  }
}
