import * as React from "react";
import { Text, View, StyleSheet, Image, Alert } from "react-native";
import Constants from "expo-constants";
import firebase from "./config/fire";
import { Provider } from "react-redux";
import store from "./store/store";

// You can import from local files
import TutorLogin from "./components/TutorLogin";
import TutorRegister from "./components/TutorRegister";
import StudentLogin from "./components/StudentLogin";
import StudentRegister from "./components/StudentRegister";
import TutorProfile from "./components/tutordashboard/TutorProfile";
import TutorDetails from "./components/tutordashboard/TutorDetails";
import StudentProfile from "./components/studentdashboard/StudentProfile";
import QuizUpload from "./components/tutordashboard/QuizUpload";
import OnlineClass from "./components/tutordashboard/OnlineClass";
import StudentClass from "./components/studentdashboard/StudentClass";
import QuizAttempt from "./components/studentdashboard/QuizAttempt";

// or any pure javascript modules available in npm
import { Card } from "react-native-paper";
import { createAppContainer, withNavigation } from "react-navigation";
import { createStackNavigator } from "react-navigation-stack";
import TutorSignUp from "./components/TutorSignup";
import StudentSignUp from "./components/StudentSignup";
import { signInAction } from "./store/actions/authActions";

class App extends React.Component {
  state = {
    user: undefined,
    isAuthenticated: false,
    pageLoading: true
  };
  componentDidMount() {
    // console.log({ props: this.props.navigation });
    firebase.auth().onAuthStateChanged(user => {
      if (user) {
        console.log({ user });
        const db = firebase.firestore();
        db.collection("users")
          .doc(user.uid)
          .get()
          .then(userDoc => {
            console.log({ userDoc });
            if (userDoc.exists) {
              if (userDoc.role) {
                const collection =
                  userDoc.role === "tutor" ? "tutors" : "students";
                db.collection(collection)
                  .doc(user.uid)
                  .get()
                  .then(doc => {
                    if (doc.exists) {
                      store.dispatch(
                        signInAction({
                          ...doc.data(),
                          id: user.uid,
                          role: userDoc.role
                        })
                      );
                      this.setState({
                        user: doc.data(),
                        isAuthenticated: true,
                        pageLoading: false
                      });
                    } else {
                      store.dispatch(
                        signInAction({ id: user.uid, role: userDoc.role })
                      );
                      this.setState({
                        user: { id: user.uid, role: userDoc.role },
                        isAuthenticated: true,
                        pageLoading: false
                      });
                    }
                  })
                  .catch(error => {
                    Alert.alert(error.message);
                    store.dispatch(
                      signInAction({ id: user.uid, role: userDoc.role })
                    );
                    this.setState({
                      user: { id: user.uid, role: userDoc.role },
                      isAuthenticated: true,
                      pageLoading: false
                    });
                  });
              }
            } else {
              Alert.alert("USER DATA NOT EXISTS!");
              store.dispatch(
                signInAction({ id: user.uid, role: userDoc.role })
              );
              this.setState({
                user: { id: user.uid, role: userDoc.role },
                isAuthenticated: true,
                pageLoading: false
              });
            }
          })
          .catch(error => {
            console.error(error.message);
            firebase
              .auth()
              .signOut()
              .then(res => this.props.navigation.replace("tutorlogin"));
            Alert.alert(error.message);
          });
      } else {
        // this.props.navigation.replace("tutorlogin");
        console.log("SIGNED OUT");
      }
    });
  }
  render() {
    return (
      // <QuizUpload />
      <Provider store={store}>
        <AppContainer />
        {/* <TutorDetails /> */}
      </Provider>
    );
  }
}

const navigator = createStackNavigator({
  tutorlogin: {
    screen: TutorLogin
  },
  tutorregister: {
    screen: TutorRegister
  },
  tutordetails: {
    screen: TutorDetails
  },
  tutorprofile: {
    screen: TutorProfile
  },
  onlineclass: {
    screen: OnlineClass
  },
  studentlogin: {
    screen: StudentLogin
  },
  studentregister: {
    screen: StudentRegister
  },
  tutorsignup: {
    screen: TutorSignUp
  },
  studentsignup: {
    screen: StudentSignUp
  }
});

const AppContainer = createAppContainer(withNavigation(navigator));
export default App;
