import { SIGNIN_SUCCESS, SIGNOUT_SUCCESS } from "./types";

export const signInAction = user => {
  return {
    type: SIGNIN_SUCCESS,
    payload: user
  };
};
export const signOutAction = () => {
  return {
    type: SIGNOUT_SUCCESS
  };
};

// ADD DETAILS
export const addUserDetailsAction = data => {
  return {
    type: ADD_USER_DETAILS,
    payload: data
  };
};
