import React from "react";
import {
  Text,
  View,
  StyleSheet,
  Image,
  ScrollView,
  TextInput,
  TouchableOpacity,
  ImageBackground,
  Button,
  ActivityIndicator,
  Alert
} from "react-native";
// import { Button } from "native-base";
import firebase from "../../config/fire";
import { firestore } from "firebase";

export default class OnlineClass extends React.Component {
  state = {
    className: "",
    course: "",
    timing: "",
    zoomLink: "",
    disabled: true,
    loading: false,
    error: {}
  };

  clearInputs = () => {
    this.setState({
      className: "",
      course: "",
      timing: "",
      zoomLink: "",
      error: {}
    });
  };

  uploadNewClass = () => {
    console.log("clicked", this.state);
    const { className, course, timing, zoomLink } = this.state;
    // this.clearInputs();
    if (className && course && timing && zoomLink) {
      this.setState({ loading: true });
      const db = firebase.firestore();
      db.collection("onlineclasses")
        .add({
          className,
          course,
          timing,
          zoomLink,
          uploadTime: firestore.Timestamp.now()
        })
        .then(res => {
          this.setState({ loading: false });
          // console.log("res---", res);
          this.clearInputs();
          this.props.navigation.goBack();
        })
        .catch(error => {
          console.error(error.message);
          this.setState({ loading: false });
          Alert.alert(error.message);
        });
    } else
      this.setState(({ error }) => ({
        error: { ...error, zoomLink: "Invalid Link" }
      }));
  };

  render() {
    const { className, course, timing, zoomLink, loading } = this.state;
    let btnDisabled =
      className && course && timing && zoomLink && !loading ? false : true;
    return (
      <ScrollView style={styles.container}>
        <Text style={styles.htext}>Online Class</Text>
        <View>
          <Text style={styles.label}>Class</Text>
          <TextInput
            style={styles.inputBox}
            underlineColorAndroid="rgba(0,0,0,0)"
            placeholder="Enter Class"
            name="className"
            keyboardType="default"
            value={this.state.className}
            required
            onChangeText={text => {
              this.setState({ className: text });
            }}
          />
        </View>
        <View>
          <Text style={styles.label}>Course</Text>
          <TextInput
            style={styles.inputBox}
            underlineColorAndroid="rgba(0,0,0,0)"
            placeholder="Enter Course"
            name="course"
            keyboardType="default"
            value={this.state.course}
            onChangeText={text => {
              this.setState({ course: text });
            }}
          />
        </View>
        <View>
          <Text style={styles.label}>Time</Text>
          <TextInput
            style={styles.inputBox}
            underlineColorAndroid="rgba(0,0,0,0)"
            placeholder="Class Timing"
            name="timing"
            keyboardType="default"
            value={this.state.timing}
            onChangeText={text => {
              this.setState({ timing: text });
            }}
          />
        </View>
        <View>
          <Text style={styles.label}>Zoom Link</Text>
          <TextInput
            style={styles.inputBox}
            underlineColorAndroid="rgba(0,0,0,0)"
            placeholder="Paste Zoom Link Here"
            name="zoomLink"
            keyboardType="default"
            value={this.state.zoomLink}
            onChangeText={text => {
              this.setState({ zoomLink: text });
            }}
          />
        </View>
        <View style={styles.btnView}>
          <Button
            style={styles.btn}
            onPress={this.uploadNewClass}
            // title={!this.state.loading ? "Submit" : <ActivityIndicator />}
            title={!this.state.loading ? "Submit" : "Loading..."}
            color="#2196F3"
            disabled={btnDisabled}
          />
        </View>
      </ScrollView>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    marginLeft: 15,
    marginRight: 15
  },
  btnView: {
    marginVertical: 10
  },
  btnText: {
    color: "white"
  },
  label: {
    fontSize: 16,
    fontWeight: "bold"
  },
  htext: {
    marginTop: 18,
    marginBottom: 52,
    fontWeight: "bold",
    fontSize: 26,
    marginHorizontal: 62
  },
  inputBox: {
    width: "100%",
    backgroundColor: "#ffffff",
    borderRadius: 5,
    borderWidth: 1,
    borderColor: "grey",
    paddingHorizontal: 16,
    paddingVertical: 10,
    fontSize: 16,
    // color: "#efefef",
    marginVertical: 10
  }
});
