import React, {Component} from 'react';  
import {StyleSheet, Text, View,StatusBar,TextInput} from 'react-native';  
import {createAppContainer} from 'react-navigation';   
import Icon from 'react-native-vector-icons/Ionicons';  
import {createMaterialTopTabNavigator} from'@react-navigation/material-top-tabs';;  

import MyTabs from './lib/router';  
const AppIndex = createAppContainer(MyTabs)  
// const Tab = createMaterialTopTabNavigator();

export default class App extends Component{  
 async componentDidMount() {
  const {text} = this.state
  let url = `https://www.googleapis.com/youtube/v3/search?part=snippet&maxResults=25&q=${text}&key=AIzaSyA1cMDS9bYZdRPf9wUX9WYbxFchCAmhQyE`
  fetch(url)
  .then(res => res.json())
   .then(result => console.log(result))
  //  let url = 'https://api.openweathermap.org/data/2.5/weather?q=karachi&units=metric&exclude=minutely&appid=a223bb812621c8a5cab0a55231c12515'
  //  let response = await fetch(url);
  //  let commits = await response.json()
  //  let lat= commits?.coord.lat
  //  let lon= commits?.coord.lon
  //  let lat_lon = `https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lon}&units=metric&exclude=minutely&appid=a223bb812621c8a5cab0a55231c12515`
  //  fetch(lat_lon)
  //  .then(res => res.json())
  //  .then(result => console.log(result))
  //  console.log(commits?.coord.lat)
  }
  state={
    text:'Karachi',
    search:[],
    data:[],
  }
  onChange=async e=>{
    const {text} = this.state
    this.setState({text:e})
   setTimeout(() => {
    let url = `http://dataservice.accuweather.com/locations/v1/cities/autocomplete?apikey=%09v3fd3sImxvMm88IOAIAYkff6eobRXME3&q=${text}`
    fetch(url)
    .then(res=>res.json())
    .then(cities => {console.log(cities)})
    // console.log(commits,text)
   }, 100);
  }
   search= ()=>{
    const {text} = this.state
    let url = `https://www.googleapis.com/youtube/v3/search?part=snippet&maxResults=25&q=${text}&key=AIzaSyA1cMDS9bYZdRPf9wUX9WYbxFchCAmhQyE`
    fetch(url)
    .then(res => res.json())
     .then(result => this.setState({data:result.items}))
    // let lat= commits?.coord.lat
    // let lon= commits?.coord.lon
    // let lat_lon = `https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lon}&units=metric&exclude=minutely&appid=a223bb812621c8a5cab0a55231c12515`
    // fetch(lat_lon)
    // .then(res => res.json())
    // .then(result => console.log(result))
  }
    render(){  
      console.log(this.state)
        return(  
          <View style={{flex:1,backgroundColor:'#40B0DD'}} >  
          <StatusBar  
              backgroundColor='black'  
              barStyle='light-content'  
          />  
          <View style={styles.header}>  
            <View style={styles.headerBar}>
              <Icon style={{color:'black'}} name='ios-menu' size={28} color='white'/>  
              <TextInput
                style={{ height: 40, borderColor: '#fff', borderWidth: 1,width:'100%',paddingLeft:'2vw',
                          paddingBottom:'0.3vw',  fontSize:'1.3vw',color:'grey',fontWeight:'500' }}
                onChangeText={this.onChange}
                value={this.state.text}
              />    
              <Icon onClick={this.search} style={{color:'black'}} name='ios-search' size={28} color='white'/>  
            </View>
          </View>  
          {Array.isArray(this.state.data) && this.state.data?.map((item,index) => {
            // let id = item?.id?.videoId
            console.log(item?.snippet?.thumbnails?.high)
            let url = `https://www.youtube.com/embed/${item.id.videoId}?enablejsapi=1&rel=0&playsinline=1&autoplay=1&showinfo=0&autohide=1&controls=0&modestbranding=1`
            return(
              <View style={{width:80,}} >
                <View>
                  <img src={item?.snippet?.thumbnails?.medium?.url} width='80' height='80' />
                </View>
                {/* <iframe id='playerId' type='text/html' width='800' height='900'
                src={url} frameborder='0'></iframe> */}
              </View>
              )
          })}
          {/* <AppIndex/>   */}
      </View>   
        )  
    }  
}  
const styles = StyleSheet.create({  
    wrapper: {  
        flex: 1,  
    },  
    header:{  
       backgroundColor:'#368AAD'
    }  ,
    headerBar:{
      flexDirection: 'row',  
      alignItems: 'center',  
      justifyContent: 'space-between',  
      backgroundColor: '#fff',  
      paddingHorizontal: 18,  
      paddingTop: 5,  
      // padding:'1vw',
      marginLeft:'1vw',
      marginRight:'1vw',
      marginBottom:'1vw',
      marginTop:'1vw',
      borderRadius:'4px'
    }
});
